// import React from 'react';
import {
  BrowserRouter as Router, Route, Switch, NavLink, withRouter,
} from 'react-router-dom';

// const App = () => (
//   <Router>
//     <Switch>
//       <Route exact path="/"><Home /></Route>
//       <Route exact path="/committee"><Committee /></Route>
//     </Switch>
