/* eslint-disable react/require-default-props */
import React from 'react';
// import {
//   BrowserRouter as Router, Route, Switch, NavLink, withRouter
// } from 'react-router-dom';
// UI
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import IconButton from '@material-ui/core/IconButton';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Routes from './routes/index';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

const App = (props) => {
  const { window } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const pages = [
    { text: 'INICIO', route: '/home' },
    { text: 'COMITÉ', route: '/committee' },
  ];
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  // eslint-disable-next-line react/prop-types
  const activeRoute = (routeName) => (props.location.pathname === routeName);

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <Divider />
      <List>
        {pages.map((page) => (
          <ListItem button key={page.text}>
            <ListItemText primary={page.text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {Routes.map((prop) => (
          <NavLink to={prop.path} style={{ textDecoration: 'none' }} key={prop.sidebarName}>
            <ListItem selected={activeRoute(prop.path)}>
              <ListItemText primary={prop.sidebarName} />
            </ListItem>
          </NavLink>
        ))}
      </List>
    </div>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap>
            CONFERENCIA
          </Typography>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>
        <div className={classes.toolbar} />
        <Router>
          <Switch>
            {Routes.map((route) => (
              <Route exact path={route.path} key={route.path}>
                <route.component />
              </Route>
            ))}
          </Switch>
          {/* { isLogin && <Redirect to="/home" />} */}
        </Router>
      </main>
    </div>
  );
  // const classes = useStyles();
  // return (
  //   <div className={classes.root}>
  //     <Router>
  //       <Paper className={classes.paper}>
  //         <MenuList>
  //           <MenuItem><Link to="/">Inicio</Link></MenuItem>
  //           <MenuItem><Link to="/committee">Comité</Link></MenuItem>
  //         </MenuList>
  //       </Paper>
  //       <Switch>
  //         <Route exact path="/">
  //           <Home />
  //         </Route>
  //         <Route exact path="/committee">
  //           <Committee />
  //         </Route>
  //       </Switch>
  //     </Router>
  //   </div>
  // );
};
App.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default withRouter(App);
